sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {
    },

    isDatePast: function(strDate){
        // Add your code here
        var rightnow = new GlideDateTime();
        var whenNeeded = new GlideDateTime(strDate);

        if(whenNeeded.before(rightnow)){
            return true;
        }
        return false;
    },

    isDateBeforeTommorow: function(strDate){
        // Add your code here
        var tommorow = new GlideDateTime();
        tommorow.addDaysLocalTime(1);
        var whenNeeded = new GlideDateTime(strDate);

        if(whenNeeded.before(tommorow)){
            return true;
        }
        return false;
    },

    type: 'NeedItUtils'
};
